package mktprograms;

public class Arithmeticoperations1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 20;
		int b = 50;
		int sum1 = a+b;
		int sum2 = b-a;
		int sum3 = a*b;
		int sum4 = b/a;

		System.out.println(sum1);
		System.out.println(sum2);
		System.out.println(sum3);
		System.out.println(sum4);



	}

}
